, ITEM_KCD
, ORIGIN_CD
, ITEM_DESC
)
VALUES
(
  #{itemNm1}
, #{itemNm2}
, #{price}
, #{itemKcd}
, #{originCd}
, #{itemDesc}
)

mapper.update:
UPDATE ITEMS
   SET IMAGE_PATH = #{imagePath}
 WHERE ITEM_NO = ${itemNo}

